    </div>
  </div>

  <!-- Content -->
  <h2 class="text-xl text-[#141219] font-medium">
    {post.title}
  </h2>

  {#if post.image}
    <img
      src={post.image}
      alt="post image"
      class="h-full w-full max-w-[700px]"
    />
  {/if}

  <p class="max-w-[700px] text-sm text-[#2B2B2B]/60 font-light">
    {post.description}
  </p>
</div>